<?php


$str = '';
function substrcount($str, $substr)
{
    $count = 0;
    for ($i = 0; $i < strlen($str)-strlen($substr)+1; $i++) {
        if (substr($str, $i, strlen($substr)) == $substr) {
            $count++;
        }
    }
    return $count;
}


$resultKeeper = 0;
for ($i = 0; $i < strlen($str); $i++) {
    for ($j = 1; $j < strlen($str) + 1; $j++) {
        $substr = substr($str, $i, $j);
        //echo $substr . ":" . substrcount($str, $substr, $i) . "=" . substrcount($str, $substr, $i) * strlen($substr) . PHP_EOL;
        $c= substrcount($str, $substr) * strlen($substr);
        $resultKeeper = max($resultKeeper,$c);
    }
    echo $c= substrcount($str, $substr, $i) * strlen($substr).PHP_EOL;
}
